import React from 'react';
import {connect} from 'react-redux';
import {AppRootStateType} from '../../redux/store';
import {compose} from 'redux';
import {WithAuthRedirect} from '../../hoc/withAuthRedirect';
import {DialogsFormik} from './DialogsFormik';
import {setNewMessageTextAC} from '../../redux/reducer/messagesPageReducer';

const mapStateToProps = (state: AppRootStateType): MapStateToPropsType => {
    return {
        dialogsData: state.messages.dialogsData,
        messagesData: state.messages.messagesData,
    }
}
type MapStateToPropsType = {
    dialogsData: DialogsDataProps[]
    messagesData: MessagesDataProps[]
}
type MapDispatchToPropsType = {
    setNewMessageTextAC: (e: string) => void
}
export type DialogsType = MapStateToPropsType & MapDispatchToPropsType


export default compose<React.ComponentType>(
    connect(mapStateToProps, {setNewMessageTextAC}),
    WithAuthRedirect,
)(DialogsFormik)

//type
type MessagesPageType = {
    dialogsData: DialogsDataProps[]
    messagesData: MessagesDataProps[]
}
type DialogsDataProps = {
    id: string
    name: string
    img: string
}
type MessagesDataProps = {
    id: string
    message: string
}
