import React from 'react';
import './App.css';
import {Navbar} from './components/Navbar/Navbar';
import {BrowserRouter, Route} from 'react-router-dom';
import {News} from './components/News/News';
import {Music} from './components/Music/Music';
import {Settings} from './components/Settings/Settings';
import {AppRootStateType} from './redux/store';
import {UsersContainer} from './components/Users/UsersContainer';
import ProfileContainer from './components/Profile/ProfileConteiner';
import HeaderContainer from './components/Header/HeaderContainer';
import DialogsContainer from './components/Dialogs/DialogsContainer';
import LoginFormik from './components/Login/LoginFormik';
import {connect, ConnectedProps} from 'react-redux';
import {initializedSuccess} from './redux/reducer/appReducer';
import {Preloader} from './common/Preloader/Preloader';

class App extends React.Component<AppContainerType> {
    componentDidMount() {
        this.props.initializedSuccess()
    }

    render() {
        if (!this.props.initialized) <Preloader/>
        return (
            <BrowserRouter>
                <div className="app-wrapper">
                    <HeaderContainer/>
                    <Navbar sitebar={this.props.storeData.sidebar}/>
                    <div className="app-wrapper-content">
                        <Route path="/dialogs" render={() => <DialogsContainer/>}/>
                        <Route path="/profile/:userId?" render={() => <ProfileContainer/>}/>
                        <Route path="/news" render={News}/>
                        <Route path="/music" render={Music}/>
                        <Route path="/settings" render={Settings}/>
                        <Route path="/users" render={() => <UsersContainer/>}/>
                        <Route path="/login" render={() => <LoginFormik/>}/>
                    </div>
                </div>
            </BrowserRouter>
        );
    }
}

const mapStateToProps = (state: AppRootStateType) => {
    return {
        initialized: state.AppReducer.initialized
    }
}
const connector = connect(mapStateToProps, {initializedSuccess})
type AppContainerType = ConnectedProps<typeof connector> & {
    storeData: AppRootStateType
    store: any
}

export default connector(App);
