import {AxiosResponse} from 'axios';
import {instance} from './apiConfig/apiConfig';

export const profileAPI = {
    getProfileUserData(userId: string) {
        return instance.get<ResponseGetUsersPages>(`profile/` + userId)
    },
    getProfileStatus(userId: string) {
        return instance.get<string>('profile/status/' + userId)
    },
    updateProfileStatus(status: string) {
        return instance.put<ResponseType>('profile/status', {status})
    }
}
export const headerAPI = {
    setAuthData() {
        return instance.get<ResponseType<{
            id: number,
            email: string,
            login: string,
        }>>('auth/me')
    }
}
export const loginAPI = {
    setLogin(email: string, password: string, rememberMe: boolean) {
        return instance.post<{ email: string, password: string, rememberMe: boolean }, AxiosResponse<ResponseType<{ userId: number }>>>
        ('auth/login', {email, password, rememberMe,})
    },
    outLogin() {
        return instance.delete<ResponseType>('auth/login')
    }
}

//type
export type ResponseType<D = {}> = {
    resultCode: number
    messages: Array<string>
    fieldsErrors: Array<string>
    data: D
}

export type ResponseGetUsersType = {
    items: Array<UserType>
    totalCount: number
    error: string | null
}
export type UserType = {
    name: string
    id: string
    uniqueUrlName: string | null
    photos: {
        small: string | null
        large: string | null
    }
    status: string | null
    followed: boolean
}

export type ResponseGetUsersPages = {
    aboutMe: string | null
    contacts: {
        facebook: string | null
        website: string | null
        vk: string | null
        twitter: string | null
        instagram: string | null
        youtube: string | null
        github: string | null
        mainLink: string | null
    }
    lookingForAJob: boolean
    lookingForAJobDescription: string | null
    fullName: string
    userId: number
    photos: {
        small: string | undefined
        large: string | undefined
    }
}
