import React from "react";
import {Field, InjectedFormProps, reduxForm} from 'redux-form'
import {ThunkLogIn} from "../../redux/reducer/authReducer";
import {connect} from "react-redux";
import {WithAuthRedirect} from "../../hoc/withAuthRedirect";
import {compose} from "redux";
import {thunkGETStatus, thunkSetProfileUserData, thunkUpdateStatus} from "../../redux/reducer/profilePageReducer";
import {withRouter} from "react-router-dom";


type LoginType = {
    ThunkLogIn: (email: string, password: string, rememberMe: boolean) => void
}
const Login: React.FC<LoginType> = ({ThunkLogIn}) => {
    debugger
    const onSubmit = (formData: LoginFormData) => {
        ThunkLogIn(formData.email, formData.password, formData.rememberMe)
    }
    return <div>
        <h1>LOGIN</h1>
        <ContactForm onSubmit={onSubmit}/>
    </div>
}

export type LoginFormData = {
    email: string
    password: string
    rememberMe: boolean
}
const LoginForm: React.FC<InjectedFormProps<LoginFormData>> = (props) => {
    return <form onSubmit={props.handleSubmit}>
        <div><Field name='email' type="text" component="input" placeholder='email'/></div>
        <div><Field name='password' type="password" component="input" placeholder='Password'/></div>
        <div><Field name='rememberMe' type="checkbox" component="input"/> remember me</div>
        <div>
            <button>Log in</button>
        </div>
    </form>
}

const ContactForm = reduxForm<LoginFormData>({form: 'login'})(LoginForm)
export default connect(null, {ThunkLogIn})(Login)
