import React from 'react';
import {Dialogs} from "./Dialogs";
import {connect} from "react-redux";
import {AppStateType} from "../../redux/storeRedux";
import {
    DialogsDataProps,
    MessagesDataProps,
    setMessageText,
    setNewMessageText
} from '../../redux/reducer/messagesPageReducer';
import {compose} from 'redux';
import {WithAuthRedirect} from "../../hoc/withAuthRedirect";

const mapStateToProps = (state: AppStateType): MapStateToPropsType => {
    return {
        dialogsData: state.messagesPageReducer.dialogsData,
        messagesData: state.messagesPageReducer.messagesData,
        // isAuth: state.authReducer.isAuth
    }
}
type MapStateToPropsType = {
    dialogsData: DialogsDataProps[]
    messagesData: MessagesDataProps[]
}
type MapDispatchToPropsType = {
    setMessageText: (e: string) => void
    setNewMessageText: () => void
}
export type DialogsType = MapStateToPropsType & MapDispatchToPropsType


export default compose<React.ComponentType>(
    connect(mapStateToProps, {setMessageText, setNewMessageText}),
    WithAuthRedirect,
)(Dialogs)
