import s from './Users.module.css';
import userAvatar from '../../assets/img/null_avatar.png';
import React from 'react';
import {NavLink} from 'react-router-dom';
import {UserType} from '../../api/userApi';
import {Paginator} from '../../common/Paginator/Paginator';
import {useDispatch} from 'react-redux';

type UsersFnType = {
    changeCurrentPageTC: (currentPage: number, pageSize: number) => void
    totalUsersCount: number
    pageSize: number
    currentPage: number
    users: Array<UserType>
    thunkUnSubscribe: (id: string) => void
    thunkSubscribe: (id: string) => void
    followingInProgress: Array<null | string>
}

export const Users: React.FC<UsersFnType> = React.memo((
    {
        changeCurrentPageTC,
        totalUsersCount,
        pageSize,
        currentPage,
        users,
        thunkUnSubscribe,
        thunkSubscribe,
        followingInProgress,
    }) => {
    const dispatch = useDispatch()
    const pagesCount = Math.ceil(totalUsersCount / pageSize)
    let pages: Array<number> = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    const onClickUnSubHandler = (id: string) => {
        thunkUnSubscribe(id)
    }
    const onClickSubHandler = (id: string) => {
        thunkSubscribe(id)
    }
    const changeCurrentPageHandler = (page: number) => {
        dispatch(changeCurrentPageTC(Page, pageSize))
    }
    return <>
        {pages.map(t => <span
            onClick={() => changeCurrentPageTC(+t, pageSize)}
            className={t === currentPage ? s.selectedPage : undefined}
            key={t}>{t}
        </span>)}
        <Paginator totalCount={totalUsersCount} pageCount={pageSize} callback={changeCurrentPageHandler}/>
        {users.map(t => {
            return (
                <div className={s.containerUser} key={t.id}>
                    <div>
                        <NavLink to={'/profile/' + t.id}>
                            <img
                                src={t.photos.small === null
                                    ? userAvatar
                                    : t.photos.small} alt="аватарка пользователей"
                            />
                        </NavLink>
                    </div>
                    {t.followed ? <button
                            disabled={followingInProgress.some(id => id === t.id
                            )}
                            onClick={() => onClickUnSubHandler(t.id)}>
                            unsubscribe
                        </button> :
                        <button
                            disabled={followingInProgress.some(id => id === t.id)}
                            onClick={() => onClickSubHandler(t.id)}>subscrib
                        </button>}
                    <div>{t.name}</div>
                </div>
            )
        })}
    </>
})
