import React from "react";
import {Field, InjectedFormProps, reduxForm} from 'redux-form'
import {ThunkLogIn} from "../../redux/reducer/authReducer";
import {connect} from "react-redux";
import {Redirect} from "react-router-dom";
import {AppStateType} from "../../redux/storeRedux";
import {useFormik} from "formik";


type LoginType = {
    ThunkLogIn: (email: string, password: string, rememberMe: boolean) => void
    isAuth: boolean
}
const LoginFormik: React.FC<LoginType> = ({ThunkLogIn, isAuth}) => {
    if (isAuth) return <Redirect to='/profile'/>
    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        onSubmit: values => {
            console.log(values)
        },
    })
    return <div>
        <h1>LOGIN</h1>
        <form onSubmit={formik.handleSubmit}>
            <input type={'email'} placeholder={'email'}/> <br/>
            <input type={'password'} placeholder={'password'}/>
            <input/>
            <button type={"submit"}>Войти</button>
        </form>
    </div>
}


//----------------------------------------connect
const pstp = (state: AppStateType) => {
    return {
        isAuth: state.authReducer.isAuth
    }
}
export default connect(pstp, {ThunkLogIn})(LoginFormik)
