import s from './Users.module.css';
import React from 'react';
import {UserType} from '../../API';

type UsersFnType = {
    thunkChangeCurrentPage: (currentPage: number, pageSize: number) => void
    totalUsersCount: number
    pageSize: number
    currentPage: number
}

export const Paginator: React.FC<UsersFnType> = React.memo((
    {
        thunkChangeCurrentPage,
        totalUsersCount,
        pageSize,
        currentPage,
    }) => {
    const pagesCount = Math.ceil(totalUsersCount / pageSize)
    let pages: Array<number> = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    return <div>
        {pages.map(t => <span
            onClick={() => thunkChangeCurrentPage(+t, pageSize)}
            className={t === currentPage ? s.selectedPage : undefined}
            key={t}>{t}
        </span>)}

    </div>
})
